								lastName: 'Localmann'
							},
							{
								id: 2,
								firstName: 'Claudia',
								lastName: 'Localmann'
							}
						];
						this.recordsStatus = 'showingRecords';
					},
					getExternalDataWithoutCallback() {
						this.simulateBlockingWork(5000);
						this.records = [
							{
								id: 1,
								firstName: 'Leonard',
								lastName: 'Externalmann'
							},
							{
								id: 2,
								firstName: 'Louis',
								lastName: 'Externalmann'
							}
						];
						this.recordsStatus = 'showingRecords';
					},
					simulateBlockingWork(ms) {
						var start = new Date().getTime();
						let now = 0;
						let difference = 0;
						for (var i = 0; i < 10000000000000000; i++) {
							now = new Date().getTime();
							difference = now - start;
							if (difference > ms) {
								break;
							}
						}
					},
					getSimulatedExternalDataWithCallback() {
