		- **useLocalStorage.tsx**
			-------------------------------------------------
			/* eslint-disable react-hooks/exhaustive-deps */
			import { useEffect, useState } from "react";

			export const useLocalStorage = (idCode: string, initialValue = "") => {
				const [value, setValue] = useState(initialValue);

				useEffect(() => {
					const _value = localStorage.getItem(idCode);
					if (_value !== null) {
						setValue(_value);
					}
				}, []);

				const save = (value: string) => {
					setValue(value);
					localStorage.setItem(idCode, value);
				};

				return { value, save };
			};			
			-------------------------------------------------
		- **PageLocalStorage.tsx**
			-------------------------------------------------
			import { useLocalStorage } from "../hooks/useLocalStorage";

			export const PageLocalStorage = () => {
				const { value: firstName, save: saveFirstName } = useLocalStorage("firstName");
				const { value: status, save: saveStatus } = useLocalStorage("status", 'not-available');

				return (
					<>
						<div className="flex gap-2 mb-3">
							<p>First Name:</p>
							<input
								value={firstName}
								onChange={(e) => saveFirstName(e.target.value)}
							/>
						</div>
						<div className="flex mb-3">
							<p className="underline cursor-pointer" onClick={() => saveStatus(status === 'available' ? 'not-available' : 'available')}>Status</p>
							<p>: {status}</p>
						</div>
						<hr className="mb-3" />
						{firstName && <p>Hello, {firstName}, you are currently marked as {status === 'available' ? 'available' : 'not available'} </p>}
					</>
				);
			};
