	const shop = (items, cbFound, cbFinished) => {
		let i = 0;
		const id = setInterval(() => {
			cbFound(items[i]);
			i++;
			if (i == items.length) {
				cbFinished('now finished');
				clearInterval(id);
			}
		}, 2000);

	}

	const foundTheItem = (item) => console.log(`They found ${item}`);

	console.log('sending backend shopping for data');
