	-----------------------------------
	<html>
	<head>
		<title>
			<%=siteTitle%>
		</title>
		<link rel="shortcut icon"
			  href="#">
		<link rel="stylesheet"
			  href="css/main.css">
		<script>
			const handleImageState = (showImages) => {
				const passwordElem = document.querySelector('.password');
				(async () => {
					const response = await fetch('http://localhost:3011/api/changeState', {
						method: 'PATCH',
						headers: { 'Content-Type': 'application/json' },
						body: JSON.stringify({
							showImages,
							password: passwordElem.value
						})
					});
					const data = await response.json();
					const message = data.message;
					const messageElem = document.querySelector('.message');
					passwordElem.value = '';
					messageElem.innerText = message;
					if(response.status == 200) {
						messageElem.classList.remove('failure');
						messageElem.classList.add('success');
					}
					if(response.status == 401) {
						messageElem.classList.remove('success');
						messageElem.classList.add('failure');
					}
				})();
			}
		</script>
	</head>
	<body>
		<h1>
			<%=siteTitle%>
		</h1>
		<%- include('_nav.ejs')
