				li.appendChild(document.createTextNode(data));
				messagesElem.appendChild(li);
				messagesElem.scrollTop = messagesElem.scrollHeight;
				messageElem.focus();
			});

			socket.on('quote', (data) => {
				quoteElem.innerText = `"${data}"`;
				fadeIn(quoteElem, 2000);
			});
		</script>
