							{currentUser.accessGroups.includes('loggedOutUsers') && (
								<NavLink to="/register">Register</NavLink>
							)}
							{currentUser.accessGroups.includes('loggedOutUsers') && (
								<NavLink to="/login">Login</NavLink>
							)}
							{currentUser.accessGroups.includes('loggedInUsers') && (
								<NavLink to="/logout">Logout</NavLink>
