			describe('test that surround() is correctly implemented', () => {
				it("returns various brackets correctly", () => {
					expect(surround('test', '[]')).toBe('[test]');
					expect(surround('test', '()')).toBe('(test)');
					expect(surround('test', '{}')).toBe('{test}');
				});
				it("returns similar characters correctly", () => {
					expect(surround('test', '**')).toBe('*test*');
					expect(surround('test', '||')).toBe('|test|');
				});
