		export const removeRandomItemFromArray = <T>(arr: T[]): T | null => {
			if (arr.length === 0) {
				return null;
			}
			const index = Math.trunc(Math.random() * arr.length);
			const selectedItem = arr[index];
			arr.splice(index, 1);
			return selectedItem;
