	import React, { useState } from 'react';
	import './App.scss';

	function App() {

		const [groupSize, setGroupSize] = useState(4);
		const [groups, setGroups] = useState([]);

		const makeGroups = (e) => {
			const persons = ["Hans", "Robert", "Ashton", "Martin", "Adele", "Brendon", "Ahmad", "Nikola", "Eva", "Georg", "Angie"];
			randomize(persons);

			const localGroups = [];

			let counter = 1;
			let group = [];
			persons.forEach(person => {
				if (counter > groupSize) {
					localGroups.push(group);
					group = [];
					counter = 1;
				}
				group.push(person);
				counter += 1;
			})
			if (group.length > 0) {
				localGroups.push(group);
			}
			setGroups([...localGroups]);

			e.preventDefault();
		}

		const randomize = (array) => {
			let currentIndex = array.length;
			let temporaryValue = null;
			let randomIndex = null;

			while (currentIndex !== 0) {
				randomIndex = Math.floor(Math.random() * currentIndex);
				currentIndex -= 1;
				temporaryValue = array[currentIndex];
				array[currentIndex] = array[randomIndex];
				array[randomIndex] = temporaryValue;
			}
			return array;
		}

		return (
			<div className="App">
				<form onSubmit={makeGroups}>
					<fieldset>
						<legend>Zufälliger Gruppeneinteiler</legend>
						<label htmlFor="groupSize">Teilnehmer pro Gruppe</label>
						<input type="text" id="groupSize" value={groupSize} onChange={(e) => setGroupSize(e.target.value)} autoFocus />
						<input className="buttonSubmit" type="submit" value="Gruppen erstellen" />
					</fieldset>
				</form>
				<div className="displayGroups">
					{groups.map((group) => {
						return (
							<ul>
								{group.map((person) => {
									return (
										<li>{person}</li>
									)
								})}
							</ul>
						)
					})}
				</div>
			</div>
		);
	}

	export default App;
