		cleanQuizItemText = cleanQuizItemText.replace('[', '');
		cleanQuizItemText = cleanQuizItemText.replace(']', '');
		const [wrongAnswer, rightAnswer] = cleanQuizItemText.split('/');
		return {
			rightAnswer,
			wrongAnswer
		}
	});

	const phrases = {
		cloze: text,
		wrong: text,
		right: text
	};
	for (const quizItem of quizItems) {
		phrases.cloze = phrases.cloze.replace(regex2, '[________]');
		phrases.wrong = phrases.wrong.replace(regex1, quizItem.wrongAnswer);
