	import Loader from './Loader.js';

	const btnInOrderElem = document.querySelector(".btnInOrder");
	const btnAllAtOnceElem = document.querySelector(".btnAllAtOnce");
	const contentElem = document.querySelector(".content");
	const loaderAreaElem = document.querySelector(".loaderArea");

	loaderAreaElem.style.display = 'none';

	const loader = new Loader();

	btnInOrderElem.onclick = () => {
		(async () => {
			contentElem.innerHTML = '';
			loaderAreaElem.style.display = 'inline';
			addLi(await loader.getData());
			addLi(await loader.getData());
			addLi(await loader.getData());
			loaderAreaElem.style.display = 'none';
		})();
	}

	btnAllAtOnceElem.onclick = () => {
		contentElem.innerHTML = '';
		loaderAreaElem.style.display = 'inline';
		(async () => {
			Promise.allSettled(Array.from(Array(3).keys()).map(m => loader.getData()), []).then((results) => results.forEach(result => {
				addLi(result.value);
				console.log(`Result "${result.value}" is ${result.status}`);
				loaderAreaElem.style.display = 'none';
			}));
		})();
	}

	function addLi(text) {
		const liElem = document.createElement('LI');
		liElem.innerText = text;
		contentElem.appendChild(liElem);
