	const cart = (customerId) => {
		const whenCreated = new Date();
		const items = [];
		return (item) => {
			items.push(item);
			return `Cart for customer ${customerId} created at ${whenCreated.toISOString()} has ${items.length} items: ${items.join(', ')}`;
		};
	};

	const cart1 = cart(111);
	const cart2 = cart(222);

	console.log(cart1('milk'));
	console.log(cart2('bread'));
	console.log(cart1('apples'));
	setTimeout(() => {
		console.log(cart1('butter'));	
	}, 1000);
