		- **shape-mixins.scss**
			-----------------------------------
			@mixin ball($bgcolor, $size) {
				background-color: $bgcolor;
				width: $size;
				height: $size;
				border-radius: 50%;
				@if $size >= 0rem {
					border: 0px;
				}
				@if $size >= 2rem {
					border: 2px solid black;
				}
				@if $size >= 4rem {
					border: 4px solid black;
				}
