			--------------------------------------
			@mixin alert-text($color: #555, $border: 2px solid #333) {
				background-color: $color;
				color: white;
				font-variant: small-caps;
				padding:.5rem;
				width: fit-content;
				border: $border;
				@content;
			}			
			--------------------------------------
