					export const capitalizeFirstLetter = (text: string) => {
						let tempFirstLetter = '';
						let r = text;

						if (r.startsWith('¿')) {
							tempFirstLetter = '¿';
							r = r.slice(1);
						}

						r = r.charAt(0).toUpperCase() + r.slice(1).toLowerCase();

						if (tempFirstLetter !== '') {
							r = tempFirstLetter + r;
						}

						return r;
					};
