		const blankNewNote = {
			body: "",
			rank: "2.5",
			app_pin: "",
		};

		export default function Home() {
			const [notes, setNotes] = useState<INote[]>([]);
			const [newNote, setNewNote] = useState<INewNote>(structuredClone(blankNewNote));

			const fetchAllNotes = async () => {
				const headers = {
					"Cache-Control": "no-cache",
					Pragma: "no-cache",
					Expires: "0",
				};
				const response = await axios.post(`${baseUrl}/api/get-notes`, {}, {
					headers,
				});
				const _notes = response.data;
				setNotes(_notes);
			};

			useEffect(() => {
				fetchAllNotes();
			}, []);

			const handleFieldChange = (fieldIdCode: string, value: string) => {
				switch (fieldIdCode) {
					case "body":
						newNote.body = value;
						break;
					case "rank":
						newNote.rank = value;
						break;
					case "app_pin":
						newNote.app_pin = value;
						break;
				}
				setNewNote(structuredClone(newNote));
			};

